b = float( input("На какое число? - ") )
# это сама матиматика))))
if what == "+":
  c = a + b
  print("Ответ - " + str(c))
elif what == "-":
  c = a - b
  print("Ответ - " + str(c))
if what == "*":
  c = a * b
  print("Ответ - " + str(c))
elif what == "/":
  c = a / b
  print("Ответ - " + str(c))
else:
  print("тут нет такого варианта действия к числам!!!" )
